import { makeStyles } from "@mui/styles";

export default makeStyles((theme) => ({
  input: {
    width: '100% !important',
    background: '#FFFFFF 0% 0% no-repeat padding-box',
    border: '1px solid #DFDFDF',
    borderRadius: '5px',
    padding: '14px',
    height: '40px',
    marginTop: 5,
    boxSizing:'border-box',

    '&::before': {
      borderBottom: 'unset',
    },

    '& .MuiInput-underline::before': {
      borderBottom: 'unset',
    },
  },
}));
