import React from 'react';
import { Grid, Typography, Button } from '@mui/material';
import DashboardStyles from '../DashboardStyles';
import ChevronRightOutlinedIcon from '@mui/icons-material/ChevronRightOutlined';

const Settings = () => {
  const styles = DashboardStyles();
  return (
      <Grid container direction="column" alignItems="center" className={styles.settingsContainer}>
        <Grid
          item
          className={styles.typoTitle}
          style={{ marginBottom: '1rem' }}
        >
          <Typography variant="h6">My Wallet</Typography>
        </Grid>
        {/* 1 */}
        <Grid
          item
          className={styles.accordionContainer}
          style={{ marginBottom: '1rem' }}
        >
          <Grid container>
            <Grid item xs={11}>
              Change Password
            </Grid>
            <Grid item xs={1}>
              <ChevronRightOutlinedIcon />
            </Grid>
          </Grid>
        </Grid>
        {/* 2 */}
        <Grid
          item
          className={styles.accordionContainer}
          style={{ marginBottom: '1rem' }}
        >
          <Grid container>
            <Grid item xs={11}>
              Auto-Lock Timer
            </Grid>
            <Grid item xs={1}>
              <ChevronRightOutlinedIcon />
            </Grid>
          </Grid>
        </Grid>
        {/* 3 */}
        <Grid
          item
          className={styles.accordionContainer}
          style={{ marginBottom: '1rem' }}
        >
          <Grid container>
            <Grid item xs={11}>
              Change Network
            </Grid>
            <Grid item xs={1}>
              <ChevronRightOutlinedIcon />
            </Grid>
          </Grid>
        </Grid>
        {/* 4 */}
        <Grid
          item
          className={styles.btnContainer}
          style={{ marginBottom: '1rem' }}
        >
          <Button variant="outlined" style={{ width: '100%' }}>
            Show Secret Recovery Phase
          </Button>
        </Grid>
        {/* 5 */}
        <Grid
          item
          className={styles.btnContainer}
          style={{ marginBottom: '1rem' }}
        >
          <Button variant="outlined" style={{ width: '100%' }}>
            Remove Wallet
          </Button>
        </Grid>
        {/* 6 */}
        <Grid item className={styles.btnContainer}  style={{ marginBottom: '1.75rem' }}>
          <Button variant="outlined" style={{ width: '100%' }}>
            Reset Secret Recovery Phase
          </Button>
        </Grid>
      </Grid>
  );
};

export default Settings;
