import { makeStyles } from "@mui/styles";

export default makeStyles((theme) => ({
  textarea: {
    width: '100%',
    background: '#FFFFFF 0% 0% no-repeat padding-box',
    border: '1px solid #DFDFDF',
    borderRadius: '5px',
    padding: '15px 15px',
    height: '4rem',
    marginTop: 2,
    boxSizing:'border-box',
    marginTop:10,

    '&::before': {
      borderBottom: 'unset',
    },

    '& .MuiInput-underline::before': {
      borderBottom: 'unset',
    },
  },
}));
