import { makeStyles } from '@mui/styles';
export default makeStyles((theme) => ({
  mainContainer: {
    minWidth: '100%',
    minHeight: '90vh',
    height: '100%',
    [theme.breakpoints.down('sm')]: {
      minHeight: '36rem',
      maxHeight: '36rem',
      minWidth: '24rem',
    },
  },
  tab: {
    minWidth: '33.3% !important',
  },
  typoTitle: {
    minWidth: '10rem',
    border: '1px solid grey',
    padding: 10,
    borderRadius: 10,
  },
  accordionContainer: {
    minWidth: '30rem',
    padding: 16,
    borderRadius: 10,
    boxShadow: '0px 0px 2px 2px #D3D3D3',
    [theme.breakpoints.down('sm')]: {
      minWidth: '100%',
    },
  },
  btnContainer: {
    minWidth: '30rem',
    [theme.breakpoints.down('sm')]: {
      minWidth: '100%',
    },
  },
  settingsContainer: {
    '&::-webkit-scrollbar': {
      width: '0.1em',
    },
  },
  // recovery
  warningContainer: {
    // minWidth: '30rem',
    minHeight: '5rem',
    padding: 10,
    borderRadius: 10,
    boxShadow: '0px 0px 2px 2px #D3D3D3',
    [theme.breakpoints.down('sm')]: {
      minWidth: '100%',
    },
  },
  showMnemContainer: {
    padding: 10,
    minWidth: '50%',
    [theme.breakpoints.down('sm')]: {
      minWidth: '100%',
    },
  },
}));
