import React, { useState } from 'react';
import Dialog from '@mui/material/Dialog';
import AppBar from '@mui/material/AppBar';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import CloseIcon from '@mui/icons-material/Close';
import Slide from '@mui/material/Slide';
import { Grid, TextField } from '@mui/material';
import Input from '../../common/Input/Input';
import DashboardStyles from '../DashboardStyles';
import { Button } from '@mui/material';

const Transition = React.forwardRef(function Transition(props, ref) {
  return <Slide direction="up" ref={ref} {...props} />;
});

const Password = ({ open, handleClose }) => {
  const styles = DashboardStyles();
  const [currentPassword, setCurrentPassword] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmNewPassword, setConfirmNewPassword] = useState('');

  const onChange = (e) => {
    switch (e.target.name) {
      case 'currentPassword':
        setCurrentPassword(e.target.value);
        break;
      case 'newPassword':
        setNewPassword(e.target.value);
        break;
      case 'confirmNewPassword':
        setConfirmNewPassword(e.target.value);
        break;
      default:
        break;
    }
  };
  return (
    <Dialog
      fullScreen
      open={open}
      onClose={handleClose}
      TransitionComponent={Transition}
      PaperProps={{
        style: {
          backgroundColor: '#2c2d30',
        },
      }}
    >
      <AppBar sx={{ position: 'relative' }}>
        <Toolbar>
          <IconButton
            edge="start"
            color="inherit"
            onClick={handleClose}
            aria-label="close"
          >
            <CloseIcon />
          </IconButton>
        </Toolbar>
      </AppBar>
      <Grid container justifyContent="center">
        <Grid item style={{ minWidth: '20rem' }}>
          <Grid container direction="column">
            <Grid item style={{ marginTop: '1rem', marginBottom: '1rem' }}>
              <Typography variant="h5" color="gainsboro" align="center">
                Change Password
              </Typography>
            </Grid>
            <Grid item style={{ marginBottom: '1rem' }}>
              <TextField
                variant="outlined"
                fullWidth
                placeholder="Current password"
                value={currentPassword}
                onChange={onChange}
              />
            </Grid>
            <Grid item style={{ marginBottom: '1rem' }}>
              <TextField
                variant="outlined"
                fullWidth
                placeholder="New password"
                value={newPassword}
                onChange={onChange}
              />
            </Grid>
            <Grid item style={{ marginBottom: '1rem' }}>
              <TextField
                variant="outlined"
                fullWidth
                placeholder="Confirm new password"
                value={confirmNewPassword}
                onChange={onChange}
                style={{backgroundColor:'#181818'}}
              />
            </Grid>
            <Grid item>
              <Typography align="center">
                <Button variant="contained">Submit</Button>
              </Typography>
            </Grid>
          </Grid>
        </Grid>
      </Grid>
      {/* <Grid container  justifyContent="center">
          <Grid item xs={12}>
            <Typography variant="h5" color="gainsboro" align="center">
              Change Password
            </Typography>
          </Grid>
          <Grid item xs={12} style={{ marginBottom: '0.85rem' }}>
            <Input
              name="currentPassword"
              label=""
              placeholder="Current password"
              value={currentPassword}
              onChange={onChange}
            />
          </Grid>{' '}
          <Grid item xs={12} style={{ marginBottom: '0.85rem' }}>
            <Input
              name="newPassword"
              label=""
              placeholder="New password"
              value={newPassword}
              onChange={onChange}
            />
          </Grid>
          <Grid item xs={12}>
            <Input
              name="confirmNewPassword"
              label=""
              placeholder="Confirm new password"
              value={confirmNewPassword}
              onChange={onChange}
            />
          </Grid>
        </Grid> */}
    </Dialog>
  );
};
export default Password;
