import { makeStyles } from "@mui/styles";

export default makeStyles((theme) => ({
  textarea: {
    width: '100% !important',
    background: '#FFFFFF 0% 0% no-repeat padding-box',
    border: '1px solid #DFDFDF',
    borderRadius: '5px',
    padding: '10px',
    height: 'auto',
    marginTop: 2,

    '&::before': {
      borderBottom: 'unset',
    },

    '& .MuiInput-underline::before': {
      borderBottom: 'unset',
    },
  },
}));
